import { Component } from '@angular/core';

@Component({
  selector: 'app-navigation',
  templateUrl: './navigation.component.html',
  styleUrls: ['./navigation.component.css']
})
export class NavigationComponent {
  menuItems = [
    {name: 'Dashboard', img: '/assets/menu1.svg'},
    {name: 'Product', img: '/assets/menu2.svg'},
    {name: 'Customers', img: '/assets/menu3.svg'},
    {name: 'Income', img: '/assets/menu4.svg'},
    {name: 'Promote', img: '/assets/menu5.svg'},
    {name: 'Help', img: '/assets/menu6.svg'}
  ];
}
