import { Component } from '@angular/core';

@Component({
  selector: 'app-navigation',
  templateUrl: './navigation.component.html',
  styleUrls: ['./navigation.component.css']
})
export class NavigationComponent {
  menuItems = [
    {name: 'Dashboard', img: '/assets/key-square (2).svg'},
    {name: 'Product', img: '/assets/3d-square 1.svg'},
    {name: 'Customers', img: '/assets/user-square 1.svg'},
    {name: 'Income', img: '/assets/wallet-money 2.svg'},
    {name: 'Promote', img: '/assets/discount-shape 1.svg'},
    {name: 'Help', img: '/assets/message-question 1.svg'}
  ];
}
